<script>
    import { afterUpdate, onMount } from "svelte";

    // prop that will be assigned the curr_index
    // of the carousel
    export let curr_index;

    // parent state that will be binded to the
    // inner carousel
    export let inner_carousel;
    
    // initially define width and height offsets as null for this
    // will be changed as size of the carousel item absolute children
    // changes
    let size_offsets = [
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
        {offset_width: null, offset_height: null},
    ];

    let max_width = null;
    let max_height = null;

    afterUpdate(() => {
        /* 
        because upon mounting binded properties to state variables are triggered 
        in the carousel items and therefore triggeres a state update we also
        calculate right then adn there the maximum of the offset heights and widths
        in the newly updated size_offsets array
        again after changing state of dynamic max content and fit content values
        of height and width by resizing window calculate the max height and width
        and change the previous max height and width states 
        */
        max_height = Math.max(...size_offsets.map((carousel_item_offset) => carousel_item_offset['offset_height']));
        max_width = Math.max(...size_offsets.map((carousel_item_offset) => carousel_item_offset['offset_width']));

        // console.log(max_height);
        // console.log(max_width);
    });
</script>

